-- ----------------------------
-- Table structure for content
-- ----------------------------
DROP TABLE IF EXISTS `content`;
CREATE TABLE `content`  (
  `cid` int(11) NOT NULL AUTO_INCREMENT COMMENT '文章id',
  `meta_id` int(11) NULL DEFAULT NULL COMMENT '分类id',
  `title` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '文章标题',
  `created` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '创建时间时间戳',
  `modified` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '修改时间时间戳',
  `text` longtext CHARACTER SET utf8 COLLATE utf8_general_ci NULL COMMENT '文章内容',
  `comment_num` int(11) NOT NULL COMMENT '评论数',
  `status` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '状态',
  `author_id` int(11) NULL DEFAULT NULL COMMENT '作者id',
  PRIMARY KEY (`cid`) USING BTREE,
  INDEX `meta_id`(`meta_id`) USING BTREE,
  INDEX `author_id`(`author_id`) USING BTREE,
  CONSTRAINT `content_ibfk_1` FOREIGN KEY (`meta_id`) REFERENCES `meta` (`mid`) ON DELETE SET NULL ON UPDATE CASCADE,
  CONSTRAINT `content_ibfk_2` FOREIGN KEY (`author_id`) REFERENCES `user` (`uid`) ON DELETE SET NULL ON UPDATE CASCADE
) ENGINE = InnoDB AUTO_INCREMENT = 34 CHARACTER SET = utf8 COLLATE = utf8_general_ci ROW_FORMAT = Compact;

